import React, { useRef, useEffect, useState } from 'react';
import { StyleSheet, Platform, View, Text, ActivityIndicator } from 'react-native';

const WebViewComponent = ({ url }) => {
  const iframeRef = useRef(null);
  const [isLoading, setIsLoading] = useState(true);

  // 判断是否为主页
  const isHomePage = url === 'https://www.sumi233.top/';

  // 在Web平台上使用iframe
  if (Platform.OS === 'web') {
    useEffect(() => {
      setIsLoading(true);

      if (iframeRef.current) {
        const iframe = iframeRef.current;

        // 重置iframe样式
        iframe.style.width = '100%';
        iframe.style.height = '100%';
        iframe.style.border = 'none';
        iframe.style.position = 'relative';

        // 重置隐藏顶栏的样式（如果是主页则不隐藏）
        if (!isHomePage) {
          iframe.style.marginTop = '0';
          iframe.style.height = '100%';
        }

        // 当iframe加载完成后处理
        const handleLoad = () => {
          try {
            // 模拟延迟以确保内容完全加载，避免顶栏闪烁
            setTimeout(() => {
              // 如果不是主页，尝试隐藏顶栏
              if (!isHomePage) {
                try {
                  // 尝试滚动iframe内容以隐藏顶栏
                  if (iframe.contentWindow) {
                    iframe.contentWindow.scrollTo(0, 60);
                  }
                } catch (scrollError) {
                  // 如果无法滚动，尝试添加负边距
                  iframe.style.marginTop = '-60px';
                  iframe.style.height = 'calc(100% + 60px)';
                }
              }

              // 加载完成，隐藏加载页面
              setIsLoading(false);
            }, 1000); // 延迟1秒，确保内容稳定
          } catch (error) {
            console.error('处理iframe加载失败:', error);
            setIsLoading(false); // 即使出错也显示内容
          }
        };

        iframe.onload = handleLoad;

        return () => {
          iframe.onload = null;
        };
      }
    }, [url, isHomePage]);

    return (
      <View style={styles.webview}>
        {isLoading && (
          <View style={styles.loadingContainer}>
            <ActivityIndicator size="large" color="#0000ff" />
            <Text style={styles.loadingText}>正在加载内容...</Text>
          </View>
        )}
        <iframe 
          ref={iframeRef}
          src={url} 
          style={{ 
            width: '100%', 
            height: '100%', 
            border: 'none',
            position: 'relative'
          }}
          title="Web Content"
        />
      </View>
    );
  }

  // 在原生平台上使用WebView
  try {
    const { WebView } = require('react-native-webview');

    // 只有非主页才注入隐藏顶栏的JavaScript代码
    const injectedJavaScript = isHomePage ? 
      'true;' : 
      `
        // 隐藏酥米的小站特定顶栏元素
        const hideSumiBlogHeader = () => {
          // 隐藏顶栏
          const siteNav = document.querySelector('.site-nav');
          if (siteNav) {
            siteNav.style.display = 'none';
            siteNav.style.visibility = 'hidden';
          }

          // 隐藏其他可能的顶栏元素
          const headers = document.querySelectorAll('header, .header, #header, .navbar, .top-bar');
          headers.forEach(element => {
            element.style.display = 'none';
            element.style.visibility = 'hidden';
          });

          // 调整内容区域
          const mainContent = document.querySelector('.main');
          if (mainContent) {
            mainContent.style.marginTop = '0 !important';
            mainContent.style.width = '100%';
          }
        };

        hideSumiBlogHeader();

        // 如果是归档页面，添加额外处理
        if (window.location.pathname.includes('archives')) {
          const mainTitle = document.querySelector('.post-title');
          if (mainTitle) {
            mainTitle.style.marginTop = '0';
          }
        }

        true;
      `;

    return (
      <View style={styles.webview}>
        {isLoading && (
          <View style={styles.loadingContainer}>
            <ActivityIndicator size="large" color="#0000ff" />
            <Text style={styles.loadingText}>正在加载内容...</Text>
          </View>
        )}
        <WebView
          source={{ uri: url }}
          style={styles.webview}
          javaScriptEnabled={true}
          domStorageEnabled={true}
          startInLoadingState={true}
          scalesPageToFit={true}
          injectedJavaScript={injectedJavaScript}
          injectedJavaScriptBeforeContentLoaded={injectedJavaScript}
          onLoadStart={() => setIsLoading(true)}
          onLoadEnd={() => {
            // 添加延迟以确保内容完全加载
            setTimeout(() => {
              setIsLoading(false);
            }, 1000);
          }}
        />
      </View>
    );
  } catch (error) {
    return (
      <View style={styles.errorContainer}>
        <Text style={styles.errorText}>无法加载WebView组件</Text>
      </View>
    );
  }
};

const styles = StyleSheet.create({
  webview: {
    flex: 1,
    position: 'relative',
  },
  loadingContainer: {
    ...StyleSheet.absoluteFillObject,
    backgroundColor: '#ffffff',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 9999,
  },
  loadingText: {
    marginTop: 10,
    fontSize: 16,
    color: '#666',
  },
  errorContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f5f5f5',
  },
  errorText: {
    fontSize: 16,
    color: '#666',
  },
});

export default WebViewComponent;
