import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  SafeAreaView,
  TouchableOpacity,
  Alert,
  ActivityIndicator,
  ScrollView,
} from 'react-native';
import { useTheme } from '../contexts/ThemeContext';

const UpdateScreen = () => {
  const { theme } = useTheme();
  const [currentVersion, setCurrentVersion] = useState('1.0.0');
  const [latestVersion, setLatestVersion] = useState('1.0.0');
  const [isChecking, setIsChecking] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [hasUpdate, setHasUpdate] = useState(false);
  const [releaseNotes, setReleaseNotes] = useState([
    '• 优化了应用性能',
    '• 添加了暗色模式支持',
    '• 添加了刷新功能',
    '• 改进了用户界面',
  ]);

  // 模拟检查更新
  const checkForUpdates = () => {
    setIsChecking(true);

    // 模拟网络请求延迟
    setTimeout(() => {
      // 模拟有新版本
      const newVersion = '1.1.0';
      setLatestVersion(newVersion);
      setHasUpdate(newVersion !== currentVersion);
      setIsChecking(false);
    }, 1500);
  };

  // 模拟更新过程
  const handleUpdate = () => {
    if (!hasUpdate) {
      Alert.alert('提示', '已是最新版本');
      return;
    }

    Alert.alert(
      '确认更新',
      `发现新版本 ${latestVersion}\n确定要更新吗？`,
      [
        { text: '取消', style: 'cancel' },
        {
          text: '更新',
          onPress: () => {
            setIsUpdating(true);

            // 模拟更新进度
            setTimeout(() => {
              setIsUpdating(false);
              Alert.alert('更新完成', '应用已更新到最新版本');
              setCurrentVersion(latestVersion);
              setHasUpdate(false);
            }, 3000);
          },
        },
      ]
    );
  };

  // 初始检查
  useEffect(() => {
    checkForUpdates();
  }, []);

  return (
    <SafeAreaView style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <ScrollView contentContainerStyle={styles.scrollContent}>
        <View style={styles.content}>
          <Text style={[styles.title, { color: theme.colors.text }]}>应用更新</Text>

          <View style={[styles.versionContainer, { backgroundColor: theme.colors.surface }]}>
            <Text style={[styles.versionLabel, { color: theme.colors.textSecondary }]}>当前版本</Text>
            <Text style={[styles.versionNumber, { color: theme.colors.text }]}>{currentVersion}</Text>
          </View>

          <View style={[styles.versionContainer, { backgroundColor: theme.colors.surface }]}>
            <Text style={[styles.versionLabel, { color: theme.colors.textSecondary }]}>最新版本</Text>
            <View style={styles.latestVersionContainer}>
              <Text style={[styles.versionNumber, { color: theme.colors.text }]}>{latestVersion}</Text>
              {hasUpdate && (
                <View style={styles.updateBadge}>
                  <Text style={styles.updateBadgeText}>新版本</Text>
                </View>
              )}
            </View>
          </View>

          {hasUpdate && (
            <View style={[styles.notesContainer, { backgroundColor: theme.colors.surface }]}>
              <Text style={[styles.notesTitle, { color: theme.colors.text }]}>更新内容</Text>
              {releaseNotes.map((note, index) => (
                <Text key={index} style={[styles.noteItem, { color: theme.colors.textSecondary }]}>
                  {note}
                </Text>
              ))}
            </View>
          )}

          <TouchableOpacity 
            style={[
              styles.checkButton, 
              { backgroundColor: theme.colors.primary },
              (isChecking || isUpdating) && styles.disabledButton
            ]}
            onPress={checkForUpdates}
            disabled={isChecking || isUpdating}
          >
            {isChecking ? (
              <ActivityIndicator color="#FFFFFF" />
            ) : (
              <Text style={styles.buttonText}>检查更新</Text>
            )}
          </TouchableOpacity>

          <TouchableOpacity 
            style={[
              styles.updateButton, 
              { backgroundColor: theme.colors.primary },
              (!hasUpdate || isUpdating) && styles.disabledButton
            ]}
            onPress={handleUpdate}
            disabled={!hasUpdate || isUpdating}
          >
            {isUpdating ? (
              <ActivityIndicator color="#FFFFFF" />
            ) : (
              <Text style={styles.buttonText}>
                {hasUpdate ? `更新到 ${latestVersion}` : '已是最新版本'}
              </Text>
            )}
          </TouchableOpacity>

          <Text style={[styles.lastChecked, { color: theme.colors.textLight }]}>
            上次检查: {new Date().toLocaleString('zh-CN')}
          </Text>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  scrollContent: {
    flexGrow: 1,
  },
  content: {
    flex: 1,
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 30,
    textAlign: 'center',
  },
  versionContainer: {
    padding: 16,
    borderRadius: 12,
    marginBottom: 16,
  },
  versionLabel: {
    fontSize: 14,
    marginBottom: 8,
  },
  versionNumber: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  latestVersionContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  updateBadge: {
    marginLeft: 12,
    backgroundColor: '#FF3B30',
    paddingHorizontal: 8,
    paddingVertical: 4,
    borderRadius: 10,
  },
  updateBadgeText: {
    color: '#FFFFFF',
    fontSize: 12,
    fontWeight: 'bold',
  },
  notesContainer: {
    padding: 16,
    borderRadius: 12,
    marginBottom: 24,
  },
  notesTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 12,
  },
  noteItem: {
    fontSize: 14,
    marginBottom: 8,
    lineHeight: 20,
  },
  checkButton: {
    padding: 16,
    borderRadius: 12,
    alignItems: 'center',
    marginBottom: 16,
  },
  updateButton: {
    padding: 16,
    borderRadius: 12,
    alignItems: 'center',
    marginBottom: 24,
  },
  disabledButton: {
    opacity: 0.6,
  },
  buttonText: {
    color: '#FFFFFF',
    fontSize: 16,
    fontWeight: 'bold',
  },
  lastChecked: {
    fontSize: 12,
    textAlign: 'center',
  },
});

export default UpdateScreen;
