import React, { useRef, useState, useEffect } from 'react';
import { View, ActivityIndicator, Platform, Text, StyleSheet, Animated } from 'react-native';
import { useTheme } from '../contexts/ThemeContext';

const WebViewComponent = ({ url }) => {
  const iframeRef = useRef(null);
  const [isLoading, setIsLoading] = useState(true);
  const [showLoading, setShowLoading] = useState(true);
  const { theme } = useTheme();

  // 动画值
  const rotateAnim = useRef(new Animated.Value(0)).current;
  const scaleAnim = useRef(new Animated.Value(1)).current;
  const fadeAnim = useRef(new Animated.Value(1)).current;

  // URL变化时重置加载状态
  useEffect(() => {
    setIsLoading(true);
    setShowLoading(true);
  }, [url]);

  // 加载动画
  useEffect(() => {
    if (isLoading) {
      // 重置动画值
      fadeAnim.setValue(1);
      rotateAnim.setValue(0);
      scaleAnim.setValue(1);

      // 启动旋转动画
      Animated.loop(
        Animated.timing(rotateAnim, {
          toValue: 1,
          duration: 1000,
          useNativeDriver: true,
        })
      ).start();

      // 启动缩放动画
      Animated.loop(
        Animated.sequence([
          Animated.timing(scaleAnim, {
            toValue: 1.2,
            duration: 800,
            useNativeDriver: true,
          }),
          Animated.timing(scaleAnim, {
            toValue: 0.9,
            duration: 800,
            useNativeDriver: true,
          })
        ])
      ).start();
    } else {
      // 淡出动画
      Animated.timing(fadeAnim, {
        toValue: 0,
        duration: 600,
        useNativeDriver: true,
      }).start(() => {
        setShowLoading(false);
      });
    }
  }, [isLoading, rotateAnim, scaleAnim, fadeAnim]);

  // Web平台使用iframe
  if (Platform.OS === 'web') {
    useEffect(() => {
      if (iframeRef.current) {
        const iframe = iframeRef.current;
        iframe.style.width = '100%';
        iframe.style.height = '100%';
        iframe.style.border = 'none';

        const handleLoad = () => {
          setTimeout(() => {
            setIsLoading(false);
          }, 300);
        };

        iframe.onload = handleLoad;

        return () => {
          iframe.onload = null;
        };
      }
    }, [url]);

    return (
      <View style={[styles.webview, { backgroundColor: theme.colors.background }]}>
        {showLoading && (
          <Animated.View 
            style={[
              styles.loadingContainer, 
              {
                backgroundColor: theme.colors.background,
                opacity: fadeAnim
              }
            ]}
          >
            <Animated.View
              style={[
                styles.animatedContainer,
                {
                  transform: [
                    { rotate: rotateAnim.interpolate({
                      inputRange: [0, 1],
                      outputRange: ['0deg', '360deg']
                    })},
                    { scale: scaleAnim }
                  ]
                }
              ]}
            >
              <ActivityIndicator size="large" color={theme.colors.primary} />
            </Animated.View>
            <Text style={[styles.loadingText, { color: theme.colors.textSecondary }]}>
              正在加载内容...
            </Text>
          </Animated.View>
        )}
        <iframe
          ref={iframeRef}
          src={url}
          style={{
            width: '100%',
            height: '100%',
            border: 'none'
          }}
        />
      </View>
    );
  }

  // 原生平台使用WebView
  try {
    const { WebView } = require('react-native-webview');
    const webViewRef = useRef(null);

    return (
      <View style={[styles.webview, { backgroundColor: theme.colors.background }]}>
        {showLoading && (
          <Animated.View 
            style={[
              styles.loadingContainer, 
              {
                backgroundColor: theme.colors.background,
                opacity: fadeAnim
              }
            ]}
          >
            <Animated.View
              style={[
                styles.animatedContainer,
                {
                  transform: [
                    { rotate: rotateAnim.interpolate({
                      inputRange: [0, 1],
                      outputRange: ['0deg', '360deg']
                    })},
                    { scale: scaleAnim }
                  ]
                }
              ]}
            >
              <ActivityIndicator size="large" color={theme.colors.primary} />
            </Animated.View>
            <Text style={[styles.loadingText, { color: theme.colors.textSecondary }]}>
              正在加载内容...
            </Text>
          </Animated.View>
        )}
        <WebView
          ref={webViewRef}
          source={{ uri: url }}
          style={{ flex: 1 }}
          javaScriptEnabled={true}
          onLoadStart={() => setIsLoading(true)}
          onLoadEnd={() => {
            setTimeout(() => {
              setIsLoading(false);
            }, 300);
          }}
        />
      </View>
    );
  } catch (e) {
    return (
      <View style={[styles.errorContainer, { backgroundColor: theme.colors.background }]}>
        <Text style={[styles.errorText, { color: theme.colors.textSecondary }]}>无法加载内容</Text>
      </View>
    );
  }
};

const styles = StyleSheet.create({
  webview: {
    flex: 1
  },
  loadingContainer: {
    ...StyleSheet.absoluteFillObject,
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1000
  },
  animatedContainer: {
    padding: 20
  },
  loadingText: {
    marginTop: 16,
    fontSize: 16
  },
  errorContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center'
  },
  errorText: {
    fontSize: 16
  }
});

export default WebViewComponent;
