import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { View, Text, StyleSheet, SafeAreaView } from 'react-native';
import WebViewComponent from './src/components/WebViewComponent';
import AboutScreen from './src/screens/AboutScreen';

const Tab = createBottomTabNavigator();

// WebView组件已恢复

function App() {
  return (
    <NavigationContainer>
      <Tab.Navigator
        screenOptions={{
          tabBarActiveTintColor: '#007AFF',
          tabBarInactiveTintColor: '#8E8E93',
          tabBarStyle: styles.tabBar,
          headerShown: false,
        }}
      >
        <Tab.Screen 
          name="Home" 
          options={{ 
            tabBarLabel: '主页',
            tabBarIcon: ({ color, size }) => (
              <View style={[styles.icon, { backgroundColor: color }]} />
            )
          }} 
        >
          {() => <WebViewComponent url="https://www.sumi233.top" />}
        </Tab.Screen>
        <Tab.Screen 
          name="Archive" 
          options={{ 
            tabBarLabel: '归档',
            tabBarIcon: ({ color, size }) => (
              <View style={[styles.icon, { backgroundColor: color }]} />
            )
          }} 
        >
          {() => <WebViewComponent url="https://www.sumi233.top/archives" />}
        </Tab.Screen>
        <Tab.Screen 
          name="Message" 
          options={{ 
            tabBarLabel: '留言板',
            tabBarIcon: ({ color, size }) => (
              <View style={[styles.icon, { backgroundColor: color }]} />
            )
          }} 
        >
          {() => <WebViewComponent url="https://www.sumi233.top/comments" />}
        </Tab.Screen>
        <Tab.Screen 
          name="About" 
          component={AboutScreen}
          options={{ 
            tabBarLabel: '关于',
            tabBarIcon: ({ color, size }) => (
              <View style={[styles.icon, { backgroundColor: color }]} />
            )
          }} 
        />
      </Tab.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  tabBar: {
    height: 60,
    paddingBottom: 8,
    paddingTop: 8,
  },
  icon: {
    width: 24,
    height: 24,
    borderRadius: 12,
  },
  // WebView样式已恢复
});

export default App;
