    paddingHorizontal: 8,
    paddingVertical: 4,
    borderRadius: 10,
  },
  updateBadgeText: {
    color: '#FFFFFF',
    fontSize: 12,
    fontWeight: 'bold',
  },
  notesContainer: {
    padding: 16,
    borderRadius: 12,
    marginBottom: 24,
  },
  notesTitle: {
