    fontWeight: 'bold',
    marginBottom: 12,
  },
  noteItem: {
    fontSize: 14,
    marginBottom: 8,
    lineHeight: 20,
  },
  versionLogItem: {
    marginBottom: 16,
    paddingBottom: 16,
    borderBottomWidth: 1,
    borderBottomColor: 'rgba(0, 0, 0, 0.1)',
  },
  versionLogHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 8,
  },
  versionLogTitle: {
    fontSize: 15,
    fontWeight: 'bold',
  },
  viewDetailsText: {
    fontSize: 14,
  },
  checkButton: {
    padding: 16,
    borderRadius: 12,
    alignItems: 'center',
    marginBottom: 16,
  },
  updateButton: {
    padding: 16,
    borderRadius: 12,
    alignItems: 'center',
    marginBottom: 24,
  },
  disabledButton: {
    opacity: 0.6,
  },
  buttonText: {
    color: '#FFFFFF',
