import React, { useRef, useEffect, useState } from 'react';
import { StyleSheet, Platform, View, Text, ActivityIndicator } from 'react-native';
import { useTheme } from '../contexts/ThemeContext';

const WebViewComponent = ({ url }) => {
  const iframeRef = useRef(null);
  const webViewRef = useRef(null);
  const [isLoading, setIsLoading] = useState(true);
  const { theme, isDarkMode } = useTheme();

  // 更宽松的主页判断逻辑，确保主页顶栏不被隐藏
  const isHomePage = url === 'https://www.sumi233.top/' || url === 'https://www.sumi233.top';

  // 切换深色模式的JavaScript代码
  const toggleDarkModeJs = (darkMode) => `
    // 创建或更新深色模式样式
    let darkModeStyle = document.getElementById('dark-mode-style');
    if (!darkModeStyle) {
      darkModeStyle = document.createElement('style');
      darkModeStyle.id = 'dark-mode-style';
      document.head.appendChild(darkModeStyle);
    }

    if (${darkMode}) {
      // 深色模式样式
      darkModeStyle.textContent = \`
        body {
          background-color: #121212 !important;
          color: #e0e0e0 !important;
        }

        /* 通用元素深色模式 */
        .site-nav, header, .header, #header, .navbar, .top-bar {
          background-color: #1e1e1e !important;
          color: #e0e0e0 !important;
          border-bottom-color: #333 !important;
        }

        /* 内容区域 */
        .main, .content, .post-content, article {
          background-color: #121212 !important;
          color: #e0e0e0 !important;
        }

        /* 卡片和容器 */
        .card, .entry, .article, .post {
          background-color: #1e1e1e !important;
          color: #e0e0e0 !important;
          border-color: #333 !important;
        }

        /* 文本元素 */
        h1, h2, h3, h4, h5, h6, p, span, a {
          color: #e0e0e0 !important;
        }

        a:hover {
          color: #FF9500 !important;
        }

        /* 输入框和表单元素 */
        input, textarea, select, button {
          background-color: #2c2c2c !important;
          color: #e0e0e0 !important;
          border-color: #444 !important;
        }

        /* 代码块 */
        pre, code {
          background-color: #2c2c2c !important;
          color: #f8f8f2 !important;
        }

        /* 链接 */
        .site-name, .site-nav a {
          color: #e0e0e0 !important;
        }

        /* 覆盖可能的亮色主题 */
        .theme-light, .light-theme, .day-theme {
          display: none !important;
        }
      \`;
    } else {
      // 移除深色模式样式
      darkModeStyle.textContent = '';
    }

    true;
  `;

  // 在Web平台上使用iframe
  if (Platform.OS === 'web') {
    useEffect(() => {
      setIsLoading(true);

      if (iframeRef.current) {
        const iframe = iframeRef.current;

        // 重置iframe样式
        iframe.style.width = '100%';
        iframe.style.height = '100%';
        iframe.style.border = 'none';
        iframe.style.position = 'relative';

        // 优化：为主页移除所有可能隐藏顶栏的样式
        if (isHomePage) {
          iframe.style.marginTop = '0';
          iframe.style.height = '100%';
        }

        // 当iframe加载完成后处理
        const handleLoad = () => {
          try {
            // 优化：减少延迟时间，提高加载速度
            setTimeout(() => {
              // 只在非主页执行隐藏顶栏操作
              if (!isHomePage) {
                try {
                  // 尝试滚动iframe内容以隐藏顶栏
                  if (iframe.contentWindow) {
                    iframe.contentWindow.scrollTo(0, 60);
                  }
                } catch (scrollError) {
                  // 如果无法滚动，尝试添加负边距
                  iframe.style.marginTop = '-60px';
                  iframe.style.height = 'calc(100% + 60px)';
                }
              }

              // 应用深色模式
              applyDarkModeToIframe(iframe, isDarkMode);

              // 加载完成，隐藏加载页面
              setIsLoading(false);
            }, 300); // 减少延迟到300ms，提高加载速度
          } catch (error) {
            console.error('处理iframe加载失败:', error);
            setIsLoading(false); // 即使出错也显示内容
          }
        };

        iframe.onload = handleLoad;

        return () => {
          iframe.onload = null;
        };
      }
    }, [url, isHomePage]);

    // 当深色模式变化时应用到iframe
    useEffect(() => {
      if (iframeRef.current && !isLoading) {
        try {
          applyDarkModeToIframe(iframeRef.current, isDarkMode);
        } catch (error) {
          console.error('应用深色模式失败:', error);
        }
      }
    }, [isDarkMode]);

    // 应用深色模式到iframe
    const applyDarkModeToIframe = (iframe, darkMode) => {
      try {
        if (iframe.contentWindow && iframe.contentWindow.document) {
          const script = iframe.contentWindow.document.createElement('script');
          script.textContent = toggleDarkModeJs(darkMode);
          iframe.contentWindow.document.head.appendChild(script);
          script.remove(); // 执行后移除脚本
        }
      } catch (error) {
        console.error('无法应用深色模式到iframe:', error);
      }
    };

    // 当深色模式变化时，重新注入JavaScript来更新网页主题
    useEffect(() => {
      if (webViewRef.current && !isLoading) {
        webViewRef.current.injectJavaScript(toggleDarkModeJs(isDarkMode));
      }
    }, [isDarkMode]);

    return (
      <View style={[styles.webview, { backgroundColor: theme.colors.background }]}>
        {isLoading && (
          <View style={[styles.loadingContainer, { backgroundColor: theme.colors.background }]}>
            <ActivityIndicator size="large" color={theme.colors.primary} />
            <Text style={[styles.loadingText, { color: theme.colors.textSecondary }]}>正在加载内容...</Text>
          </View>
        )}
        <iframe 
          ref={iframeRef}
          src={url} 
          style={{ 
            width: '100%', 
            height: '100%', 
            border: 'none',
            position: 'relative'
          }}
          title="Web Content"
          loading="lazy" // 添加懒加载优化
        />
      </View>
    );
  }

  // 在原生平台上使用WebView
  try {
    const { WebView } = require('react-native-webview');

    // 只有非主页才注入隐藏顶栏的JavaScript代码
    // 生成注入的JavaScript代码，包含深色模式切换
        const injectedJavaScript = `
          // 隐藏酥米的小站特定顶栏元素
          const hideSumiBlogHeader = () => {
            // 隐藏顶栏
            const siteNav = document.querySelector('.site-nav');
            if (siteNav) {
              siteNav.style.display = 'none';
              siteNav.style.visibility = 'hidden';
            }

            // 隐藏其他可能的顶栏元素
            const headers = document.querySelectorAll('header, .header, #header, .navbar, .top-bar');
            headers.forEach(element => {
              element.style.display = 'none';
              element.style.visibility = 'hidden';
            });

            // 调整内容区域
            const mainContent = document.querySelector('.main');
            if (mainContent) {
              mainContent.style.marginTop = '0 !important';
              mainContent.style.width = '100%';
            }
          };

          // 只在非主页隐藏顶栏
          ${!isHomePage ? 'hideSumiBlogHeader();' : ''}

          // 如果是归档页面，添加额外处理
          if (window.location.pathname.includes('archives')) {
            const mainTitle = document.querySelector('.post-title');
            if (mainTitle) {
              mainTitle.style.marginTop = '0';
            }
          }

          // 应用深色模式
          ${toggleDarkModeJs(isDarkMode)}

          true;
      `;

    return (
      <View style={[styles.webview, { backgroundColor: theme.colors.background }]}>
        {isLoading && (
          <View style={[styles.loadingContainer, { backgroundColor: theme.colors.background }]}>
            <ActivityIndicator size="large" color={theme.colors.primary} />
            <Text style={[styles.loadingText, { color: theme.colors.textSecondary }]}>正在加载内容...</Text>
          </View>
        )}
        <WebView
          ref={webViewRef}
          source={{ uri: url }}
          style={[styles.webview, { backgroundColor: theme.colors.background }]}
          javaScriptEnabled={true}
          domStorageEnabled={true}
          startInLoadingState={true}
          scalesPageToFit={true}
          injectedJavaScript={injectedJavaScript}
          injectedJavaScriptBeforeContentLoaded={injectedJavaScript}
          onLoadStart={() => setIsLoading(true)}
          onLoadEnd={() => {
            // 优化：减少延迟时间
            setTimeout(() => {
              setIsLoading(false);
            }, 300); // 减少延迟到300ms
          }}
          // 添加性能优化参数
          javaScriptCanOpenWindowsAutomatically={false}
          originWhitelist={['*']}
          // 根据主题模式设置WebView背景
          backgroundColor={theme.colors.background}
        />
      </View>
    );
  } catch (error) {
    return (
      <View style={[styles.errorContainer, { backgroundColor: theme.colors.background }]}>
        <Text style={[styles.errorText, { color: theme.colors.textSecondary }]}>无法加载WebView组件</Text>
      </View>
    );
  }
};

const styles = StyleSheet.create({
  webview: {
    flex: 1,
    position: 'relative',
  },
  loadingContainer: {
    ...StyleSheet.absoluteFillObject,
    backgroundColor: '#ffffff',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 9999,
  },
  loadingText: {
    marginTop: 10,
    fontSize: 16,
    color: '#666',
  },
  errorContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f5f5f5',
  },
  errorText: {
    fontSize: 16,
    color: '#666',
  },
});

export default WebViewComponent;
