import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { View, Text, StyleSheet, SafeAreaView } from 'react-native';
import AboutScreen from './src/screens/AboutScreen';

const Tab = createBottomTabNavigator();

// 简单的占位组件，用于测试导航
const PlaceholderScreen = ({ route }) => {
  const urls = {
    Home: 'https://www.sumi233.top',
    Archive: 'https://www.sumi233.top/archives',
    Message: 'https://www.sumi233.top/comments'
  };
  
  return (
    <SafeAreaView style={styles.placeholderContainer}>
      <Text style={styles.placeholderTitle}>{route.name}</Text>
      <Text style={styles.placeholderUrl}>URL: {urls[route.name]}</Text>
      <Text style={styles.placeholderText}>WebView组件将在这里加载网页内容</Text>
    </SafeAreaView>
  );
};

function App() {
  return (
    <NavigationContainer>
      <Tab.Navigator
        screenOptions={{
          tabBarActiveTintColor: '#007AFF',
          tabBarInactiveTintColor: '#8E8E93',
          tabBarStyle: styles.tabBar,
          headerShown: false,
        }}
      >
        <Tab.Screen 
          name="Home" 
          component={PlaceholderScreen}
          options={{ 
            tabBarLabel: '主页',
            tabBarIcon: ({ color, size }) => (
              <View style={[styles.icon, { backgroundColor: color }]} />
            )
          }} 
        />
        <Tab.Screen 
          name="Archive" 
          component={PlaceholderScreen}
          options={{ 
            tabBarLabel: '归档',
            tabBarIcon: ({ color, size }) => (
              <View style={[styles.icon, { backgroundColor: color }]} />
            )
          }} 
        />
        <Tab.Screen 
          name="Message" 
          component={PlaceholderScreen}
          options={{ 
            tabBarLabel: '留言板',
            tabBarIcon: ({ color, size }) => (
              <View style={[styles.icon, { backgroundColor: color }]} />
            )
          }} 
        />
        <Tab.Screen 
          name="About" 
          component={AboutScreen}
          options={{ 
            tabBarLabel: '关于',
            tabBarIcon: ({ color, size }) => (
              <View style={[styles.icon, { backgroundColor: color }]} />
            )
          }} 
        />
      </Tab.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  tabBar: {
    height: 60,
    paddingBottom: 8,
    paddingTop: 8,
  },
  icon: {
    width: 24,
    height: 24,
    borderRadius: 12,
  },
  placeholderContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#F5F5F5',
    padding: 20,
  },
  placeholderTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
    color: '#333',
  },
  placeholderUrl: {
    fontSize: 16,
    color: '#007AFF',
    marginBottom: 20,
    textAlign: 'center',
  },
  placeholderText: {
    fontSize: 16,
    color: '#666',
    textAlign: 'center',
  },
});

export default App;
