import React from 'react';
import { View, Text, StyleSheet, SafeAreaView, Switch, TouchableOpacity, ScrollView, Alert } from 'react-native';
import { useTheme } from '../contexts/ThemeContext';
import { clearCacheAndCookies } from '../components/WebViewComponent';

const SettingsScreen = ({ navigation, onOpenUpdate, onOpenAboutInfo }) => {
  const { theme, isDarkMode, followSystem, toggleTheme, toggleFollowSystem } = useTheme();

  // 处理清除缓存和Cookie
  const handleClearCache = () => {
    Alert.alert(
      '确认清除',
      '确定要清除所有网页缓存和Cookie吗？',
      [
        { text: '取消', style: 'cancel' },
        { 
          text: '确定', 
          onPress: clearCacheAndCookies,
          style: 'destructive'
        }
      ]
    );
  };

  return (
    <SafeAreaView style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <ScrollView contentContainerStyle={styles.scrollContent}>
        <View style={styles.content}>
          <Text style={[styles.title, { color: theme.colors.text }]}>设置</Text>

          <View style={styles.settingItemsContainer}>
            {/* 跟随系统主题开关 */}
            <View style={[styles.settingItem, { backgroundColor: theme.colors.surface }]}>
              <Text style={[styles.settingLabel, { color: theme.colors.text }]}>跟随系统主题</Text>
              <Switch
                value={followSystem}
                onValueChange={toggleFollowSystem}
                trackColor={{ false: '#767577', true: theme.colors.primary }}
                thumbColor="#FFFFFF"
              />
            </View>

            {/* 暗色模式开关 */}
            <View style={[styles.settingItem, { backgroundColor: theme.colors.surface }]}>
              <Text style={[styles.settingLabel, { color: theme.colors.text }]}>暗色模式</Text>
              <Switch
                value={isDarkMode}
                onValueChange={toggleTheme}
                disabled={followSystem}
                trackColor={{
                  false: '#767577',
                  true: followSystem ? '#CCCCCC' : theme.colors.primary
                }}
                thumbColor="#FFFFFF"
              />
            </View>

            {/* 检查更新选项 */}
            <TouchableOpacity 
              style={[styles.settingItem, { backgroundColor: theme.colors.surface }]}
              onPress={onOpenUpdate}
              activeOpacity={0.7}
            >
              <Text style={[styles.settingLabel, { color: theme.colors.text }]}>检查更新</Text>
              <Text style={[styles.settingArrow, { color: theme.colors.textSecondary }]}>›</Text>
            </TouchableOpacity>

            {/* 清除缓存和Cookie选项 */}
            <TouchableOpacity 
              style={[styles.settingItem, { backgroundColor: theme.colors.surface }]}
              onPress={handleClearCache}
              activeOpacity={0.7}
            >
              <Text style={[styles.settingLabel, { color: theme.colors.text }]}>清除缓存和Cookie</Text>
              <Text style={[styles.settingArrow, { color: theme.colors.textSecondary }]}>›</Text>
            </TouchableOpacity>

            {/* 关于选项 */}
            <TouchableOpacity 
              style={[styles.settingItem, { backgroundColor: theme.colors.surface }]}
              onPress={onOpenAboutInfo}
              activeOpacity={0.7}
            >
              <Text style={[styles.settingLabel, { color: theme.colors.text }]}>关于</Text>
              <Text style={[styles.settingArrow, { color: theme.colors.textSecondary }]}>›</Text>
            </TouchableOpacity>
          </View>

          <Text style={[styles.copyright, { color: theme.colors.textLight }]}>© 2025 酥米的小站</Text>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  scrollContent: {
    flexGrow: 1,
  },
  content: {
    flex: 1,
    padding: 20,
    alignItems: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginTop: 20,
    marginBottom: 30,
  },
  settingItemsContainer: {
    width: '100%',
    marginBottom: 30,
  },
  settingArrow: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  settingItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 16,
    borderRadius: 12,
    marginBottom: 12,
  },
  settingLabel: {
    fontSize: 16,
  },
  settingValue: {
    fontSize: 16,
  },
  copyright: {
    fontSize: 14,
    marginTop: 'auto',
    marginBottom: 40,
  },
});

export default SettingsScreen;
