import React from 'react';
import { View, Text, StyleSheet, SafeAreaView } from 'react-native';

const AboutScreen = () => {
  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.content}>
        <Text style={styles.title}>关于酥米的小站</Text>
        <Text style={styles.version}>版本: 1.0.0</Text>
        <Text style={styles.description}>这是一个基于React Native开发的跨平台Web套壳应用</Text>
        <Text style={styles.url}>网站地址: https://www.sumi233.top</Text>
        <Text style={styles.copyright}>© 2023 酥米的小站</Text>
      </View>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F5F5F5',
  },
  content: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
    color: '#333',
  },
  version: {
    fontSize: 16,
    color: '#666',
    marginBottom: 10,
  },
  description: {
    fontSize: 16,
    color: '#666',
    marginBottom: 10,
    textAlign: 'center',
  },
  url: {
    fontSize: 16,
    color: '#007AFF',
    marginBottom: 30,
    textAlign: 'center',
  },
  copyright: {
    fontSize: 14,
    color: '#999',
    position: 'absolute',
    bottom: 40,
  },
});

export default AboutScreen;
