import React, { useState } from 'react';
import { View, Text, StyleSheet, SafeAreaView, Switch, TouchableOpacity, ScrollView, Alert } from 'react-native';
import { useTheme } from '../contexts/ThemeContext';
import { clearCacheAndCookies } from '../components/WebViewComponent';

const AboutScreen = ({ navigation, onOpenUpdate }) => {
  const { theme, isDarkMode, followSystem, toggleTheme, toggleFollowSystem } = useTheme();
  const [isSettingsExpanded, setIsSettingsExpanded] = useState(false);

  // 处理清除缓存和Cookie
  const handleClearCache = () => {
    Alert.alert(
      '确认清除',
      '确定要清除所有网页缓存和Cookie吗？',
      [
        { text: '取消', style: 'cancel' },
        { 
          text: '确定', 
          onPress: clearCacheAndCookies,
          style: 'destructive'
        }
      ]
    );
  };

  return (
    <SafeAreaView style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <ScrollView contentContainerStyle={styles.scrollContent}>
        <View style={styles.content}>
          <Text style={[styles.title, { color: theme.colors.text }]}>关于酥米的小站</Text>
          <Text style={[styles.version, { color: theme.colors.textSecondary }]}>版本: 1.0.0</Text>
          <Text style={[styles.description, { color: theme.colors.textSecondary }]}>这是一个基于React Native开发的跨平台Web套壳应用</Text>
          <Text style={[styles.url, { color: theme.colors.primary }]}>网站地址: https://www.sumi233.top</Text>

          {/* 设置选项 */}
          <View style={styles.settingsSection}>
            <TouchableOpacity 
              style={[styles.sectionHeader, { backgroundColor: theme.colors.surface }]}
              onPress={() => setIsSettingsExpanded(!isSettingsExpanded)}
              activeOpacity={0.7}
            >
              <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>设置</Text>
              <Text style={[styles.sectionArrow, { color: theme.colors.textSecondary }]}>
                {isSettingsExpanded ? '▲' : '▼'}
              </Text>
            </TouchableOpacity>

            {isSettingsExpanded && (
              <View style={styles.settingItemsContainer}>
                {/* 跟随系统主题开关 */}
                <View style={[styles.settingItem, { backgroundColor: theme.colors.surface }]}>
                  <Text style={[styles.settingLabel, { color: theme.colors.text }]}>跟随系统主题</Text>
                  <Switch
                    value={followSystem}
                    onValueChange={toggleFollowSystem}
                    trackColor={{ false: '#767577', true: theme.colors.primary }}
                    thumbColor="#FFFFFF"
                  />
                </View>

                {/* 暗色模式开关 */}
                <View style={[styles.settingItem, { backgroundColor: theme.colors.surface }]}>
                  <Text style={[styles.settingLabel, { color: theme.colors.text }]}>暗色模式</Text>
                  <Switch
                    value={isDarkMode}
                    onValueChange={toggleTheme}
                    disabled={followSystem}
                    trackColor={{
                      false: '#767577',
                      true: followSystem ? '#CCCCCC' : theme.colors.primary
                    }}
                    thumbColor="#FFFFFF"
                  />
                </View>

                {/* 检查更新选项 */}
                <TouchableOpacity 
                  style={[styles.settingItem, { backgroundColor: theme.colors.surface }]}
                  onPress={onOpenUpdate}
                  activeOpacity={0.7}
                >
                  <Text style={[styles.settingLabel, { color: theme.colors.text }]}>检查更新</Text>
                  <Text style={[styles.settingValue, { color: theme.colors.primary }]}>v1.0.0</Text>
                </TouchableOpacity>

                {/* 清除缓存和Cookie选项 */}
                <TouchableOpacity 
                  style={[styles.settingItem, { backgroundColor: theme.colors.surface }]}
                  onPress={handleClearCache}
                  activeOpacity={0.7}
                >
                  <Text style={[styles.settingLabel, { color: theme.colors.text }]}>清除缓存和Cookie</Text>
                  <Text style={[styles.settingArrow, { color: theme.colors.textSecondary }]}>›</Text>
                </TouchableOpacity>
              </View>
            )}
          </View>

          <Text style={[styles.copyright, { color: theme.colors.textLight }]}>© 2023 酥米的小站</Text>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  scrollContent: {
    flexGrow: 1,
  },
  content: {
    flex: 1,
    padding: 20,
    alignItems: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginTop: 40,
    marginBottom: 20,
  },
  version: {
    fontSize: 16,
    marginBottom: 10,
  },
  description: {
    fontSize: 16,
    marginBottom: 30,
    textAlign: 'center',
  },
  url: {
    fontSize: 16,
    marginBottom: 40,
    textAlign: 'center',
  },
  settingsSection: {
    width: '100%',
    marginBottom: 30,
  },
  sectionHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 16,
    borderRadius: 12,
    marginBottom: 4,
  },
  sectionArrow: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  settingItemsContainer: {
    marginTop: 8,
  },
  settingArrow: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  settingItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 16,
    borderRadius: 12,
    marginBottom: 12,
  },
  settingLabel: {
    fontSize: 16,
  },
  settingValue: {
    fontSize: 16,
  },
  copyright: {
    fontSize: 14,
    marginTop: 'auto',
    marginBottom: 40,
  },
});

export default AboutScreen;
