import React, { useRef, useState, useEffect } from 'react';
import { View, ActivityIndicator, Platform, Text, StyleSheet, Animated } from 'react-native';
import { WebView } from 'react-native-webview';
import { useTheme } from '../contexts/ThemeContext';

const WebViewComponent = ({ url }) => {
  const iframeRef = useRef(null);
  const webViewRef = useRef(null);
  const [isLoading, setIsLoading] = useState(true);
  const [showLoading, setShowLoading] = useState(true); // 额外的状态来控制加载动画的显示
  const { theme, isDarkMode } = useTheme();

  // 动画值引用
  const rotateAnim = useRef(new Animated.Value(0)).current;
  const scaleAnim = useRef(new Animated.Value(1)).current;
  const fadeAnim = useRef(new Animated.Value(1)).current;

  // 检查是否为主页
  const isHomePage = url.includes('sumiblog.com');

  // 深色模式切换的JavaScript注入代码（仅用于原生平台）
  const toggleDarkModeJs = `
    (function() {
      // 检查是否已经应用了dark-mode类
      const htmlElement = document.documentElement;
      const isDarkMode = ${isDarkMode};

      if (isDarkMode && !htmlElement.classList.contains('dark-mode')) {
        htmlElement.classList.add('dark-mode');
        htmlElement.classList.remove('light-mode');
      } else if (!isDarkMode && !htmlElement.classList.contains('light-mode')) {
        htmlElement.classList.add('light-mode');
        htmlElement.classList.remove('dark-mode');
      }

      // 为深色模式应用更全面的样式
      if (isDarkMode) {
        // 隐藏浅色模式的图标
        const lightIcons = document.querySelectorAll('.icon-light');
        lightIcons.forEach(icon => {
          icon.style.display = 'none';
        });

        // 显示深色模式的图标
        const darkIcons = document.querySelectorAll('.icon-dark');
        darkIcons.forEach(icon => {
          icon.style.display = 'block';
        });
      } else {
        // 显示浅色模式的图标
        const lightIcons = document.querySelectorAll('.icon-light');
        lightIcons.forEach(icon => {
          icon.style.display = 'block';
        });

        // 隐藏深色模式的图标
        const darkIcons = document.querySelectorAll('.icon-dark');
        darkIcons.forEach(icon => {
          icon.style.display = 'none';
        });
      }

      return true;
    })();
  `;

  // 重置加载状态当URL改变时
  useEffect(() => {
    setIsLoading(true);
    setShowLoading(true);
  }, [url]);

  // 优化的加载动画效果
  useEffect(() => {
    // 旋转动画 - 使用循环旋转
    const rotateLoop = Animated.loop(
      Animated.timing(rotateAnim, {
        toValue: 1,
        duration: 1000,
        useNativeDriver: true,
      })
    );

    // 缩放动画 - 使用循环缩放
    const scaleLoop = Animated.loop(
      Animated.sequence([
        Animated.timing(scaleAnim, {
          toValue: 1.2,
          duration: 800,
          useNativeDriver: true,
        }),
        Animated.timing(scaleAnim, {
          toValue: 0.9,
          duration: 800,
          useNativeDriver: true,
        })
      ])
    );

    if (isLoading) {
      // 重置动画值
      fadeAnim.setValue(1);
      rotateAnim.setValue(0);
      scaleAnim.setValue(1);

      // 启动所有动画
      rotateLoop.start();
      scaleLoop.start();
    } else {
      // 执行淡出动画
      Animated.timing(fadeAnim, {
        toValue: 0,
        duration: 600,
        useNativeDriver: true,
      }).start(() => {
        // 动画完成后停止其他动画并隐藏加载视图
        rotateLoop.stop();
        scaleLoop.stop();
        setShowLoading(false);
      });
    }

    // 清理函数
    return () => {
      rotateLoop.stop();
      scaleLoop.stop();
    };
  }, [isLoading, rotateAnim, scaleAnim, fadeAnim]);

  // 在Web平台上使用iframe
  if (Platform.OS === 'web') {
    useEffect(() => {
      if (iframeRef.current) {
        const iframe = iframeRef.current;

        // 重置iframe样式
        iframe.style.width = '100%';
        iframe.style.height = '100%';
        iframe.style.border = 'none';
        iframe.style.position = 'relative';

        // 优化：为主页移除所有可能隐藏顶栏的样式
        if (isHomePage) {
          iframe.style.marginTop = '0';
          iframe.style.height = '100%';
        }

        // 加载完成处理
        const handleLoad = () => {
          try {
            // 优化：减少延迟时间，提高加载速度
            setTimeout(() => {
              // 只在非主页执行隐藏顶栏操作
              if (!isHomePage) {
                try {
                  // 尝试滚动iframe内容以隐藏顶栏
                  if (iframe.contentWindow) {
                    iframe.contentWindow.scrollTo(0, 60);
                  }
                } catch (scrollError) {
                  // 如果无法滚动，尝试添加负边距
                  iframe.style.marginTop = '-60px';
                  iframe.style.height = 'calc(100% + 60px)';
                }
              }

              // 设置加载完成状态，触发淡出动画
              setIsLoading(false);
            }, 300); // 减少延迟到300ms，提高加载速度
          } catch (error) {
            console.error('处理iframe加载失败:', error);
            setIsLoading(false); // 即使出错也显示内容
          }
        };

        iframe.onload = handleLoad;

        return () => {
          iframe.onload = null;
        };
      }
    }, [url, isHomePage]);

    return (
      <View style={[styles.webview, { backgroundColor: theme.colors.background }]}>
        {showLoading && (
          <Animated.View 
            style={[
              styles.loadingContainer, 
              {
                backgroundColor: theme.colors.background,
                opacity: fadeAnim,
                transform: [{ scale: scaleAnim.interpolate({
                  inputRange: [0, 1],
                  outputRange: [0.9, 1]
                })}]
              }
            ]}
          >
            <Animated.View
              style={[
                styles.animatedContainer,
                {
                  transform: [
                    { rotate: rotateAnim.interpolate({
                      inputRange: [0, 1],
                      outputRange: ['0deg', '360deg']
                    })},
                    { scale: scaleAnim }
                  ]
                }
              ]}
            >
              <ActivityIndicator size="large" color={theme.colors.primary} />
            </Animated.View>
            <Animated.Text
              style={[
                styles.loadingText,
                { 
                  color: theme.colors.textSecondary,
                  opacity: fadeAnim,
                  transform: [{ scale: scaleAnim }]
                }
              ]}
            >
              正在加载内容...
            </Animated.Text>
          </Animated.View>
        )}
        <iframe
          ref={iframeRef}
          src={url}
          style={{
            width: '100%',
            height: '100%',
            border: 'none',
            backgroundColor: theme.colors.background
          }}
        />
      </View>
    );
  }
  // 在原生平台上使用WebView
  else {
    // 深色模式变化时注入JavaScript（仅在原生平台）
    useEffect(() => {
      if (Platform.OS !== 'web' && webViewRef.current) {
        webViewRef.current.injectJavaScript(toggleDarkModeJs);
      }
    }, [isDarkMode]);

    return (
      <View style={[styles.webview, { backgroundColor: theme.colors.background }]}>
        {showLoading && (
          <Animated.View 
            style={[
              styles.loadingContainer, 
              {
                backgroundColor: theme.colors.background,
                opacity: fadeAnim,
                transform: [{ scale: scaleAnim.interpolate({
                  inputRange: [0, 1],
                  outputRange: [0.9, 1]
                })}]
              }
            ]}
          >
            <Animated.View
              style={[
                styles.animatedContainer,
                {
                  transform: [
                    { rotate: rotateAnim.interpolate({
                      inputRange: [0, 1],
                      outputRange: ['0deg', '360deg']
                    })},
                    { scale: scaleAnim }
                  ]
                }
              ]}
            >
              <ActivityIndicator size="large" color={theme.colors.primary} />
            </Animated.View>
            <Animated.Text
              style={[
                styles.loadingText,
                { 
                  color: theme.colors.textSecondary,
                  opacity: fadeAnim,
                  transform: [{ scale: scaleAnim }]
                }
              ]}
            >
              正在加载内容...
            </Animated.Text>
          </Animated.View>
        )}
        <WebView
          ref={webViewRef}
          source={{ uri: url }}
          style={{
            flex: 1,
            backgroundColor: theme.colors.background
          }}
          javaScriptEnabled={true}
          domStorageEnabled={true}
          onLoadStart={() => setIsLoading(true)}
          onLoadEnd={() => {
            // 优化：减少延迟时间
            setTimeout(() => {
              // 设置加载完成状态，触发淡出动画
              setIsLoading(false);
            }, 300); // 减少延迟到300ms
          }}
          injectedJavaScript={`
            // 隐藏网页顶栏
            (function() {
              // 创建一个CSS样式来隐藏header和导航栏
              const style = document.createElement('style');
              style.textContent = 
                '.site-header, header, nav { display: none !important; }' +
                'body { padding-top: 0 !important; margin-top: 0 !important; }';
              document.head.appendChild(style);

              // 滚动到顶部
              window.scrollTo(0, 0);

              // 如果是归档页面，添加额外的样式处理
              if (window.location.href.includes('/archives')) {
                // 可以添加特定于归档页面的样式调整
              }

              // 应用初始深色模式设置
              const htmlElement = document.documentElement;
              const isDarkMode = ${isDarkMode};

              if (isDarkMode) {
                htmlElement.classList.add('dark-mode');
                htmlElement.classList.remove('light-mode');
              } else {
                htmlElement.classList.add('light-mode');
                htmlElement.classList.remove('dark-mode');
              }

              return true;
            })();
          `}
          onError={(error) => {
            console.error('WebView加载错误:', error);
            setIsLoading(false);
          }}
        />
      </View>
    );
  }
};

const styles = StyleSheet.create({
  webview: {
    flex: 1
  },
  loadingContainer: {
    ...StyleSheet.absoluteFillObject,
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1000
  },
  animatedContainer: {
    padding: 20,
    borderRadius: 10,
    justifyContent: 'center',
    alignItems: 'center'
  },
  loadingText: {
    marginTop: 16,
    fontSize: 16,
    textAlign: 'center'
  },
  errorContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20
  },
  errorText: {
    fontSize: 16,
    textAlign: 'center',
    color: '#ff3b30'
  }
});

export default WebViewComponent;
