import React from 'react';
import { View, Text, StyleSheet, SafeAreaView, Switch, TouchableOpacity, ScrollView } from 'react-native';
import { useTheme } from '../contexts/ThemeContext';

const AboutScreen = ({ navigation }) => {
  const { theme, isDarkMode, followSystem, toggleTheme, toggleFollowSystem } = useTheme();

  return (
    <SafeAreaView style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <ScrollView contentContainerStyle={styles.scrollContent}>
        <View style={styles.content}>
          <Text style={[styles.title, { color: theme.colors.text }]}>关于酥米的小站</Text>
          <Text style={[styles.version, { color: theme.colors.textSecondary }]}>版本: 1.0.0</Text>
          <Text style={[styles.description, { color: theme.colors.textSecondary }]}>这是一个基于React Native开发的跨平台Web套壳应用</Text>
          <Text style={[styles.url, { color: theme.colors.primary }]}>网站地址: https://www.sumi233.top</Text>

          {/* 主题设置选项 */}
          <View style={styles.settingsSection}>
            <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>显示设置</Text>

            {/* 跟随系统主题开关 */}
            <View style={[styles.settingItem, { backgroundColor: theme.colors.surface }]}>
              <Text style={[styles.settingLabel, { color: theme.colors.text }]}>跟随系统主题</Text>
              <Switch
                value={followSystem}
                onValueChange={toggleFollowSystem}
                trackColor={{ false: '#767577', true: theme.colors.primary }}
                thumbColor="#FFFFFF"
              />
            </View>

            {/* 暗色模式开关 */}
            <View style={[styles.settingItem, { backgroundColor: theme.colors.surface }]}>
              <Text style={[styles.settingLabel, { color: theme.colors.text }]}>暗色模式</Text>
              <Switch
                value={isDarkMode}
                onValueChange={toggleTheme}
                disabled={followSystem}
                trackColor={{
                  false: '#767577',
                  true: followSystem ? '#CCCCCC' : theme.colors.primary
                }}
                thumbColor="#FFFFFF"
              />
            </View>
          </View>

          {/* 更新应用选项 */}
          <TouchableOpacity 
            style={[styles.updateButton, { backgroundColor: theme.colors.surface }]}
            onPress={() => navigation.navigate('Update')}
            activeOpacity={0.7}
          >
            <Text style={[styles.updateButtonText, { color: theme.colors.primary }]}>检查更新</Text>
          </TouchableOpacity>

          <Text style={[styles.copyright, { color: theme.colors.textLight }]}>© 2023 酥米的小站</Text>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  scrollContent: {
    flexGrow: 1,
  },
  content: {
    flex: 1,
    padding: 20,
    alignItems: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginTop: 40,
    marginBottom: 20,
  },
  version: {
    fontSize: 16,
    marginBottom: 10,
  },
  description: {
    fontSize: 16,
    marginBottom: 30,
    textAlign: 'center',
  },
  url: {
    fontSize: 16,
    marginBottom: 40,
    textAlign: 'center',
  },
  settingsSection: {
    width: '100%',
    marginBottom: 30,
  },
  sectionTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  settingItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 16,
    borderRadius: 12,
    marginBottom: 12,
  },
  settingLabel: {
    fontSize: 16,
  },
  updateButton: {
    width: '100%',
    padding: 16,
    borderRadius: 12,
    alignItems: 'center',
    marginBottom: 60,
  },
  updateButtonText: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  copyright: {
    fontSize: 14,
    marginTop: 'auto',
    marginBottom: 40,
  },
});

export default AboutScreen;
