import React from 'react';
import { View, Text, StyleSheet, SafeAreaView, ScrollView, TouchableOpacity } from 'react-native';
import { useTheme } from '../contexts/ThemeContext';
import { APP_CONFIG } from '../constants/Config';

const AboutScreen = ({ navigation, onOpenSettings, onOpenAboutInfo }) => {
  const { theme } = useTheme();

  return (
    <SafeAreaView style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <ScrollView contentContainerStyle={styles.scrollContent}>
        <View style={styles.content}>

          {/* 关于信息 */}
          <View style={styles.aboutContainer}>
            <Text style={[styles.appName, { color: theme.colors.text }]}>{APP_CONFIG.APP_NAME}</Text>
            <Text style={[styles.version, { color: theme.colors.textSecondary }]}>版本: {APP_CONFIG.VERSION}</Text>
            <Text style={[styles.description, { color: theme.colors.textSecondary }]}>酥米的小站App端，基于react native开发</Text>
            <Text style={[styles.website, { color: theme.colors.primary }]}>网站地址: {APP_CONFIG.WEBSITE_URL}</Text>
          </View>

          {/* 设置入口 */}
          <TouchableOpacity 
            style={[styles.settingsButton, { backgroundColor: theme.colors.surface }]}
            onPress={onOpenSettings}
            activeOpacity={0.7}
          >
            <Text style={[styles.settingsButtonText, { color: theme.colors.text }]}>设置</Text>
            <Text style={[styles.settingsArrow, { color: theme.colors.textSecondary }]}>›</Text>
          </TouchableOpacity>

          {/* 关于选项 */}
          <TouchableOpacity 
            style={[styles.settingItem, { backgroundColor: theme.colors.surface }]}
            onPress={onOpenAboutInfo}
            activeOpacity={0.7}
          >
            <Text style={[styles.settingLabel, { color: theme.colors.text }]}>关于</Text>
            <Text style={[styles.settingArrow, { color: theme.colors.textSecondary }]}>›</Text>
          </TouchableOpacity>

          <Text style={[styles.copyright, { color: theme.colors.textLight }]}>{APP_CONFIG.COPYRIGHT}</Text>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  scrollContent: {
    flexGrow: 1,
  },
  content: {
    flex: 1,
    padding: 20,
    alignItems: 'center',
    justifyContent: 'center',
  },
  aboutContainer: {
    alignItems: 'center',
    marginBottom: 40,
  },
  appName: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  version: {
    fontSize: 16,
    marginBottom: 12,
  },
  description: {
    fontSize: 16,
    marginBottom: 20,
    textAlign: 'center',
  },
  website: {
    fontSize: 16,
    marginBottom: 30,
    textAlign: 'center',
  },
  settingsButton: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    width: '100%',
    padding: 16,
    borderRadius: 12,
    marginBottom: 30,
  },
  settingsButtonText: {
    fontSize: 16,
  },
  settingsArrow: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  copyright: {
    fontSize: 14,
    marginTop: 'auto',
    marginBottom: 40,
  },
});

export default AboutScreen;
